mapper.select:
		SELECT * FROM (
		    SELECT ROWNUM rnum, tb.* FROM (
			     SELECT num, i.userId, m.nickName, subject,TO_CHAR(m.created, 'YYYY-MM-DD') created,hitCount
			        FROM infoReqBoard i JOIN member m ON i.userId=m.userId  
                    
                         
			                  
		                 
	                
	                ORDER BY num DESC
	
	        ) tb WHERE ROWNUM <= #{end}
	    ) WHERE rnum >= #{start}
