			from promotelist p
			join study s on p.s_num=s.s_num			

			 where range<2 and (DBMS_LOB.INSTR(p.subject,'${keyword}') > 0 or DBMS_LOB.INSTR(p.content,'${keyword}') > 0 )
			order by created desc
		 )tb where rownum <=#{end}
)where rnum >=#{start}

