mapper.select:
	SELECT * FROM (
  		SELECT ROWNUM examNum, tb.* FROM (
    		SELECT ed.num, examinfoname, exam, ex_One, ex_two, ex_three, ex_four FROM EXAMTESTLIST etl
			    JOIN EXAMDB ED ON etl.num = ed.examNum
			    JOIN EXAMPLENUMBER ep ON ed.num = ep.num
				    
		) tb WHERE ROWNUM <= #{end}
	) where examNum >= #{start}
